import React from 'react';
import axios from 'axios';
import useDatafetch from '../useDatafetch';
function CountryContainer(){
    const {country,loading} = useDatafetch();
    console.log("12345",country);
    const check =[country];
    console.log("checking",loading)
    return(
        <div className="d-flex flex-wrap m-7 tryyy">
            {check?.map((countryy,id) =>{
                    return (
                    <div key={id} className="try p-2 text-center hover-bg-dark">
                        <div className="w-100 p-3 text-center border try">
                        <img className="w-75 tryy" src={countryy.data.Countries[id].flag} alt={countryy.data.Countries[id].Country}/>
                        <h4 className="m-1">{countryy.data.Countries[id].Country}</h4>
                        <h6 className="m-2">cases:{countryy.data.Countries[id].TotalConfirmed}</h6>
                        <h6 className="m-2">Recoverd:{countryy.data.Countries[id].TotalRecovered}</h6>
                        <h6 className="m-2">Deaths:{countryy.data.Countries[id].TotalDeaths}</h6>
                        </div>
                    </div>
                    )}
            )} 
        </div>
    )
}
export default CountryContainer;
