import axios from 'axios';
import {useEffect, useState} from 'react';

const useDatafetch = (country) =>{
    const options = {
        method: 'GET',
        url: 'https://covid-19-coronavirus-statistics.p.rapidapi.com/v1/total',
        params: {country: `${country}`},
        headers: {
          'x-rapidapi-key': '4541ed7c42mshcdc05dbb3aaa8f7p164215jsnfac22ce85ec3',
          'x-rapidapi-host': 'covid-19-coronavirus-statistics.p.rapidapi.com'
        }
      };
      
      axios.request(options).then(function (response) {
          console.log("countrywise",response.data);
      }).catch(function (error) {
          console.error(error);
      });
    const url_2 = "https://restcountries.eu/rest/v2/all";
    const [flagName,setFlagName] = useState([]);
    const url="https://disease.sh/v3/covid-19/all";
    const [datas,setDatas] = useState([]);

    const fetch = async () =>{
        const {data} = await axios.get(url);
        setDatas(data);
        //console.log(datas);

        const flag = await axios.get(url_2);
        setFlagName(flag.data);
        //console.log("flag",flagName);
   };
   useEffect(() => fetch(),[]);
   return {datas,flagName}
}
export default useDatafetch;
