import axios from 'axios';
import {useEffect, useState} from 'react';

const useDatafetch = () =>{
    const url_2 = "https://restcountries.eu/rest/v2/all";
    const url="https://disease.sh/v3/covid-19/all";
    const [datas,setDatas] = useState([]);
    const [country,setCountry]=useState([]);
    const [loading,setLoading]=useState(true);
    const fetch = async () =>{
        const {data} = await axios.get(url);
        setDatas(data);
        //console.log(datas);
        const details = await axios('https://api.covid19api.com/summary');
        const flag = await axios.get(url_2);
        var i;
        var j;
        for (i=0;i<details.data.Countries.length;i++){
            for (j=0;j<flag.data.length;j++){
                if (details.data.Countries[i].Country==flag.data[j].name){
                    details.data.Countries[i]["flag"]=flag.data[j].flag;
                };
            };
        };
        setCountry(details);
        console.log("america",country);
        //console.log("flag",flagName);

   };
   useEffect(() => {
    fetch();
    if (country.data.legth>0){
        setLoading(false);
    }
    },[]);
   return {datas,country,loading}
}
export default useDatafetch;
