        flags.map(flagg =>{
        detailss.map(detail =>{
            if (detail.data.Countries.Country==flagg.data.name){
                    detail["flag"]=flagg.data.flag
            };
            console.log("this is combined",details)
        })})
        setFlagName(flag.data);
        //console.log("flag",flagName);

   };
   useEffect(() => {
    fetch();
    },[]);
   return {datas,flagName}
}
export default useDatafetch;
