import React from 'react';
import axios from 'axios';
import useDatafetch from '../useDatafetch';
function CountryContainer(){
    const options = {
        method: 'GET',
        url: 'https://covid-19-coronavirus-statistics.p.rapidapi.com/v1/total',
        // params: {country: 'Canada'},
        headers: {
          'x-rapidapi-key': '4541ed7c42mshcdc05dbb3aaa8f7p164215jsnfac22ce85ec3',
          'x-rapidapi-host': 'covid-19-coronavirus-statistics.p.rapidapi.com'
        }
      };
      
      axios.request(options).then(function (response) {
          console.log("countrywise",response.data);
      }).catch(function (error) {
          console.error(error);
      });
    const {flagName} = useDatafetch();
    return(
        <div className="d-flex flex-wrap m-7 tryyy">
            {flagName.map((flags,id) =>{
                    return (
                    <div key={id} className="try p-2 text-center hover-bg-dark">
                        <div className="w-100 p-3 text-center border try">
                        <img className="w-75 tryy" src={flags.flag} alt={flags.name}/>
                        <h4 className="m-1">{flags.name}</h4>
                        <h6 className="m-2">cases:{}</h6>
                        <h6 className="m-2">Recoverd:{}</h6>
                        <h6 className="m-2">Deaths:{}</h6>
                        </div>
                    </div>
                    )
                })}
        </div>
    )
}
export default CountryContainer;
